const PaymentModal = ({ isOpen, onClose, onSubmit, userInfo, setUserInfo }) => {
    if (!isOpen) return null;

    const handleChange = (e) => {
        setUserInfo({ ...userInfo, [e.target.name]: e.target.value });
    };

    const modalStyle = {
        position: 'fixed',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        backgroundColor: 'white',
        padding: '40px',
        zIndex: '1000',
        borderRadius: '8px',
        boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
        width: '400px',
        textAlign: 'center',
    };

    const inputStyle = {
        display: 'block',
        width: '100%',
        padding: '10px',
        margin: '10px 0 20px',
        boxSizing: 'border-box'
    };

    const buttonStyle = {
        width: 'calc(50% - 4px)',
        padding: '10px',
        margin: '0 2px',
        cursor: 'pointer',
    };

    return (
        <div style={modalStyle}>
            <h2>Enter Payment Details</h2>
            <input
                type="text"
                name="name"
                value={userInfo.name}
                onChange={handleChange}
                placeholder="Your Name"
        
            />
            <input
                type="email"
                name="email"
                value={userInfo.email}
                onChange={handleChange}
                placeholder="Your Email"

            />
            <input
                type="text"
                name="contact"
                value={userInfo.contact}
                onChange={handleChange}
                placeholder="Your Contact Number"
         
            />
            <button style={buttonStyle} onClick={() => onSubmit(userInfo)}>Proceed to Pay</button>
            <button style={buttonStyle} onClick={onClose}>Cancel</button>
        </div>
    );
};

export default PaymentModal;
